import React from 'react';

class Test extends React.Component{
    constructor () {
        // this.onclick = this.onclick.bind(this)
        super()
        this.state = {
            a:1
        }
    }

    onclick (a) {
        a =  a+1;
        this.setState ({
            a:a
        })
    }


    render () {
        let a = this.state;
        return (
            <div>
                <p style={{marginLeft:100,marginTop:100}}>'贱贱饶武健' +{a} </p>
                <button onClick={this.onclick(a)}>点击</button>
            </div>
        )

    } 
}

export  default Test;
