import { FETCH,DELETE,SEARCH,RESET,EDIT,ISEDITING } from "../actionType/actionType";
// import { action } from "mobx";

//获取数据
export const createFetchAction = (res) => {
    return {
        type : FETCH,
        payload:res,
    }
}

//删除
export const deleteAction = (key) => {
    return {
        type : DELETE,
        payload:key,
    }
}

//搜索
export const SearchAction = (res) => {
    return {
        type : SEARCH,
        payload:res,
    }
}

//重置
export const ResetAction = () => {
    return {
        type : RESET,
    }
}






//编辑开关
export const EditAction = () => {
    return {
        type : EDIT,
    }
}

//编辑进行中
export const isEditingAction = () => {
    return {
        type : ISEDITING,
    }
}
