import { FETCH,DELETE,SEARCH } from "../actionType/actionType";
import { action } from "mobx";

//获取数据
export const createFetchAction = (res) => {
    return {
        type : FETCH,
        payload:res,
    }
}

//删除
export const createDeleteAction = (res) => {
    return {
        type : DELETE,
        payload:res,
    }
}

//搜索
export const createSearchAction = (res) => {
    return {
        type : SEARCH,
        payload:res,
    }
}
