import React from 'react';
import { Input,Button } from 'antd';

const { Search } = Input;

class Myinput extends React.Component{
    constructor(){
        super();
        this.state =({
            value: '',
        })
        this.onSearch1 = this.onSearch1.bind(this);
        this.Change = this.Change.bind(this);
        this.State1 = this.State1.bind(this);
    }
    onSearch1(value ){
        this.props.handSearch(value);
    }
    Change(){
        this.props.handReset();
        this.setState({
            value: '',
        });
    }
    State1(e){
     this.setState({
         value: e.target.value,
     });
    }



    render(){
        return <div>
        <Search
          placeholder="input search text"
          enterButton="搜索"
          size="large"
          onSearch={value => this.onSearch1(value) }
          value = {this.state.value}
          onChange={e => this.State1(e)}
        />
        <Button onClick={this.Change} >重置</Button>
      </div>
    }
}
export default Myinput;
