import React, {Component} from 'react';
import { Table, Input, InputNumber, Popconfirm, Form, Spin, Button, Modal } from 'antd';
import 'antd/dist/antd.css';
import CollectionCreateForm from '../Create/Create'
// import Search from '../Search/Search'
import Myinput from '../Input/Input'; 
import store from '../../store/store';
import {createFetchAction,deleteAction,SearchAction,ResetAction,EditAction} from '../../action/action';
import {connect} from 'react-redux';
import { stat } from 'fs';


//把todolist从state中解构出来
let mapSetstateToProps = (state) => {
  console.log(state)
  return {
    todolist : state.todolist,
  }
}

let mapDisPatchToprops = (dispatch) => {
  return {
    fetch:(res)=>{
      dispatch(createFetchAction(res))
    },
    delete:(_id)=>{
      dispatch(deleteAction(_id))
    },
    // search:(name)=>{
    //   dispatch(SearchAction(name))
    // },
    // reset:()=>{
    //   dispatch(ResetAction())
    // },
    // edit:(_id)=>{
    //   dispatch(EditAction(_id))
    // },
    // isEditing:(_id)=>{
    //   dispatch(EditAction(_id))
    // },
  }
}


const EditableContext = React.createContext();


//修改类
class EditableCell extends React.Component {
    getInput = () => {
      if (this.props.inputType === 'number') {
        return <InputNumber />;
      }
      return <Input />;
    };

    renderCell = ({ getFieldDecorator }) => {
      const {
        editing,
        dataIndex,
        title,
        inputType,
        record,
        index,
        children,
        ...restProps
      } = this.props;
      return (
        <td {...restProps}>
          {editing ? (
            <Form.Item style={{ margin: 0 }}>
              {getFieldDecorator(dataIndex, {
                rules: [
                  {
                    required: true,
                    message: `Please Input ${title}!`,
                  },
                ],
                initialValue: record[dataIndex],
              })(this.getInput())}
            </Form.Item>
          ) : (
            children
          )}
        </td>
      );
    };

    render() {

      return <EditableContext.Consumer>{this.renderCell}</EditableContext.Consumer>;
    }
}

//主组件
@connect(mapSetstateToProps,mapDisPatchToprops)
class EditableTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      editingKey: '',
      visible: false, 
      loading: false,
      dataSource: '',
      list:'',
    };

    //订阅store,监听listening，来设置数据状态
    store.subscribe(this.listening);
    let data = this.props.todolist.data;

    this.handSearch = this.handSearch.bind(this)
    this.handReset = this.handReset.bind(this)


    this.columns = [
      {
        title: '名称',
        dataIndex: 'name',
        editable: true,
      },

      {
        title: '简介',
        dataIndex: 'desc',
        editable: true,
      },

      {
        title: '网址',
        dataIndex: 'url',
      },

      {
        title: '类型',
        dataIndex: 'type',
        editable: true,
      },

      {
        title: '时间',
        dataIndex: 'time',
        editable: true,
      },
      {
        title: '修改',
        dataIndex: 'operation',
        render: (text, record) => {
          const { editingKey } = this.state;
          const editable = this.isEditing(record);
          return editable ? (
            <span>
              <EditableContext.Consumer>
                {form => (
                  <a
                    onClick={() => this.save(form, record._id)}
                    style={{ marginRight: 8 }}
                  >
                    保存
                  </a>
                )}
              </EditableContext.Consumer>
              <Popconfirm title="确定要取消吗?" onConfirm={() => this.cancel(record._id)}>
                <a>取消</a>
              </Popconfirm>
            </span>
          ) : (
            <a disabled={editingKey !== ''} onClick={() => this.edit(record._id)}>
              修改
            </a>
          );
        },
      },
      {
        title: '删除',
        dataIndex: 'delete',

        render: (text, record) =>
          data.length >= 0 ? (
            <Popconfirm title="你确定要删除吗？" onConfirm={() =>this.deletadata(record._id)}>
              <a>删除</a>
            </Popconfirm>
          ) : null,
      },
    ];
  }

  //改变数据状态方法
  listening = () => {
    this.setState ({
      data:store.getState().todolist.data,
    }) 
  }
// 删除方法
deletadata=(id)=>{
  fetch ('http://localhost:8070/delete',{
    method:'POST',
    body:JSON.stringify(id) 
  })
  this.reset()
}

  //编辑组件四大方法
  isEditing = record => record._id === this.state.editingKey;

  cancel = () => {
    this.setState({ editingKey: '' });
  };

  save(form, _id) {
    form.validateFields((error, row) => {
      if (error) {
        return;
      }
    row.id=_id

    });
  }

  edit(key) {
    this.setState({ editingKey: key });
  }


  handleCancel = () => {
    this.setState({ visible: false });
  };

// 添加
increate=(value)=>{
    fetch ('http://localhost:8070/add',{
        method:'POST',
        body:JSON.stringify(value) 
      })
}


  //添加方法
  handleCreate = () => {
    const { form } = this.formRef.props;
    form.validateFields((err, values) => {
      if (err) {
        return;
      }


      console.log(values)




      let time = (new Date()).valueOf();
      values.key = time;
    this.increate(values)
      form.resetFields();

    });
  };

  showModal = () => {
    this.setState({ visible: true });
  };

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  //搜索方法
  handSearch(value){
    this.props.search(value);
  }

  //重置方法
  handReset(){
    this.props.reset();
  }

  // 重置的方法
  reset=()=>{
    fetch('http://localhost:8070/select')
    .then(res => res.json())
    .then( res => this.props.fetch(res))
  }

  //生命周期  加载后
  componentDidMount(){
    this.reset()
  }


  render() {
    let data = this.props.todolist.data;
    const components = {
      body: {
        cell: EditableCell,
      },
    };

    const columns = this.columns.map(col => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onCell: record => ({
          record,
          inputType: col.dataIndex === 'age' ? 'number' : 'text',
          dataIndex: col.dataIndex,
          title: col.title,
          editing: this.isEditing(record),
        }),
      };
    });

    return (
      <div>
        { data
          ?<div>

              <Myinput handSearch={this.handSearch} handReset={this.handReset}></Myinput>
              <Button type="primary" onClick={this.showModal} style={{marginLeft:18}}>
                创建
              </Button>
              <CollectionCreateForm
                wrappedComponentRef={this.saveFormRef}
                visible={this.state.visible}
                onCancel={this.handleCancel}
                onCreate={this.handleCreate}
              />
              <EditableContext.Provider value={this.props.form}>
                <Table
                  components={components}
                  bordered
                  dataSource={data}
                  columns={columns}
                  rowClassName="editable-row"
                  pagination={{
                    onChange: this.cancel,
                  }}
                  rowKey={(data)=>data.key}
                />
              </EditableContext.Provider>
            </div>
          : <Spin style={{marginLeft:'900px',marginTop:'200px'}} tip="加载中..." size='large'/>
        }
      </div>

    );
  }
}

const EditableFormTable = Form.create()(EditableTable);

class Mytable extends Component{ 
    render(){
        return <EditableFormTable />
    }
}

export default Mytable;
