import React, {Component} from 'react';
import { Table, Input, InputNumber, Popconfirm, Form, Spin, Button, Modal, Radio } from 'antd';
import 'antd/dist/antd.css';
import EditableCell from '../Modify/Modify'

const data = null;
const EditableContext = React.createContext();

//创建， 类
const CollectionCreateForm = Form.create({ name: 'form_in_modal' })(
  // eslint-disable-next-line
  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="创建"
          okText="创建"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="名称">
              {getFieldDecorator('who', {
                rules: [{ required: true, message: 'Please input the title of collection!' }],
              })(<Input />)}
            </Form.Item>
            <Form.Item label="类型">
              {getFieldDecorator('type')(<Input type="textarea" />)}
            </Form.Item>
            <Form.Item label="网址">
              {getFieldDecorator('url')(<Input type="textarea" />)}
            </Form.Item>
            <Form.Item label="内容">
              {getFieldDecorator('desc')(<Input type="textarea" />)}
            </Form.Item>
            {/* <Form.Item className="collection-create-form_last-form-item">
              {getFieldDecorator('modifier', {
                initialValue: 'public',
              })(
                <Radio.Group>
                  <Radio value="public">Public</Radio>
                  <Radio value="private">Private</Radio>
                </Radio.Group>,
              )}
            </Form.Item> */}
          </Form>
        </Modal>
      );
    }
  },
);

//修改类


class EditableTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      data,
      editingKey: '',
      visible: false, 
      loading: false,
    };
    this.columns = [
      {
        title: '名称',
        dataIndex: 'who',
        editable: true,
      },
      {
        title: '类型',
        dataIndex: 'type',
        editable: true,
      },
      {
        title: '网址',
        dataIndex: 'url',
        render :text => <a href={text} target='_blank'>链接</a>

      },
      {
        title: '创建时间',
        dataIndex: 'createdAt',
        editable: true,

      },
      {
        title: '发表时间',
        dataIndex: 'publishedAt',
        editable: true,
      },
      {
        title: '内容',
        width: '25%',
        dataIndex: 'desc',
        editable: true,
      },
      {
        title: '修改',
        dataIndex: 'operation',
        render: (text, record) => {
          const { editingKey } = this.state;
          const editable = this.isEditing(record);
          return editable ? (
            <span>
              <EditableCell>
                {form => (
                  <a
                    onClick={() => this.save(form, record.key)}
                    style={{ marginRight: 8 }}
                  >
                    保存
                  </a>
                )}
              </EditableCell>
              <Popconfirm title="确定要取消吗?" onConfirm={() => this.cancel(record.key)}>
                <a>取消</a>
              </Popconfirm>
            </span>
          ) : (
            <a disabled={editingKey !== ''} onClick={() => this.edit(record.key)}>
              修改
            </a>
          );
        },
      },
      {
        title: '删除',
        dataIndex: 'delete',
        render: (text, record) =>
          this.state.data.length >= 1 ? (
            <Popconfirm title="你确定要删除吗？" onConfirm={() => this.handleDelete(record.key)}>
              <a>删除</a>
            </Popconfirm>
          ) : null,
      },
    ];

    this.setSearchTopStories = this.setSearchTopStories.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.showModal = this.showModal.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleCreate = this.handleCreate.bind(this);
    this.saveFormRef = this.saveFormRef.bind(this);
  }

  isEditing = record => record.key === this.state.editingKey;

  handleDelete = key => {
    console.log(key)
    const data = [...this.state.data];
    this.setState({ data: data.filter(item => item.key !== key) });
  };

  cancel = () => {
    this.setState({ editingKey: '' });
  };

  save(form, key) {
    form.validateFields((error, row) => {
      if (error) {
        return;
      }
      const newData = [...this.state.data];
      const index = newData.findIndex(item => key === item.key);
      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, {
          ...item,
          ...row,
        });
        this.setState({ data: newData, editingKey: '' });
      } else {
        newData.push(row);
        this.setState({ data: newData, editingKey: '' });
      }
    });
  }

  edit(key) {
    this.setState({ editingKey: key });
  }

  //添加方法
  handleCancel = () => {
    this.setState({ visible: false });
  };
  handleCreate = () => {
    const { form } = this.formRef.props;
    const { data} = this.state;
    form.validateFields((err, values) => {
      if (err) {
        return;
      }
      let time = (new Date()).valueOf();
      values.key = time;
      data.push(values);
      form.resetFields();
      this.setState({ data: data, visible: false });
    });
  };

  showModal = () => {
    this.setState({ visible: true });
  };

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  //获取数据后更新组件
  setSearchTopStories(res){
    let arr = [];
    let data1 = res.results;
    for(let key in data1){
      arr.push(data1[key])
    }
    let arr1 = [].concat.apply([],arr);
    let arr2 = [];
    for(let i=0; i<arr1.length; i++){
      arr2.push({
        key: arr1[i]._id,
        createdAt: arr1[i].createdAt,
        desc: arr1[i].desc,
        publishedAt: arr1[i].publishedAt,
        who: arr1[i].who,
        type: arr1[i].type,
        url: arr1[i].url,
        used: true,
      });
    }
    this.setState({data:arr2})
  }

  //生命周期  加载后
  componentDidMount(){
    fetch('http://gank.io/api/today')
    .then(res => res.json())
    .then( res => this.setSearchTopStories(res));

  }


  render() {
    let {data, loading, visible} = this.state;
    const components = {
      body: {
        cell: EditableCell,
      },
    };

    const columns = this.columns.map(col => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onCell: record => ({
          record,
          inputType: col.dataIndex === 'age' ? 'number' : 'text',
          dataIndex: col.dataIndex,
          title: col.title,
          editing: this.isEditing(record),
        }),
      };
    });

    return (
      <div>
        { data
          ?<div>
              <Button type="primary" onClick={this.showModal} style={{marginLeft:10}}>
                创建
              </Button>
              <CollectionCreateForm
                wrappedComponentRef={this.saveFormRef}
                visible={this.state.visible}
                onCancel={this.handleCancel}
                onCreate={this.handleCreate}
              />
              <EditableContext.Provider value={this.props.form}>
                <Table
                  components={components}
                  bordered
                  dataSource={this.state.data}
                  columns={columns}
                  rowClassName="editable-row"
                  pagination={{
                    onChange: this.cancel,
                  }}
                />
              </EditableContext.Provider>
            </div>
          : <Spin style={{marginLeft:'900px',marginTop:'200px'}}/>
        }
      </div>

    );
  }
}

const EditableFormTable = Form.create()(EditableTable);

class Mytable extends Component{
    render(){
        return <EditableFormTable />
    }
}

export default Mytable;

// ReactDOM.render(<EditableFormTable />, mountNode);
