  setSearchTopStories(resule){
    let arr = [];
    let data = resule.results;
    for(let i in data){
      arr.push(data[i])
    }
    let arr2 = [].concat.apply([],arr);
    this.setState({dataSource: arr2});
  };
  //生命周期 加载后
  componentDidMount(){
    fetch('http://gank.io/api/today')
    .then(resource=> resource.json())
    .then(resule => this.setSearchTopStories(resule));
  };

  // 创建 添加方法

  handleCancel = () => {
    this.setState({ visible: false });
  };

  handleCreate = () => {
    const { form } = this.formRef.props;
    const { dataSource} = this.state;
    form.validateFields((err, values) => {
      if (err) {
        return;
      }
      console.log(values);
      dataSource.push(values);
      form.resetFields();
      this.setState({ dataSource:dataSource });
    });
  };

  showModal = () => {
    let {oyffw} = this.state;
    console.log(oyffw)

    // const newData = {
      //     key: count,
      //     _id: count+1,
      //     who: 'fingdo',
