      //     publishedAt: `2019-04-10T00:00:00.0Z`,
      //     desc: `极简 Android 组件化方案 — AppJoint`,
      //   };

    this.setState({
      visible: true,
    });
  };
  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  render() {
    const { dataSource,loading, visible } = this.state;
    const components = {
      body: {
        row: EditableFormRow,
        cell: EditableCell,
      },
    };
    const columns = this.columns.map(col => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onCell: record => ({
          record,
          editable: col.editable,
          dataIndex: col.dataIndex,
          title: col.title,
          handleSave: this.handleSave,
        }),
      };
    });
    return (
      <div>
        {(dataSource)
          ?<div>
            <Button type="primary" onClick={this.showModal} style={{marginLeft:10}}>
              创建
            </Button>
            <CollectionCreateForm
              wrappedComponentRef={this.saveFormRef}
              visible={this.state.visible}
              onCancel={this.handleCancel}
              onCreate={this.handleCreate}
            />
              <Table
                components={components}
                rowClassName={() => 'editable-row'}
                bordered
                dataSource={dataSource}
                columns={columns}
                rowKey = {dataSource=>dataSource._id}
              />
            </div>
          : <Spin style={{marginLeft:'900px',marginTop:'200px'}}/>
        }
      </div>
    );
  }
