import {FETCH,DELETE,SEARCH} from '../actionType/actionType';

//初始化数据
const initstate = {
    data:'',
}

//最终的数据处理结果
export const todolist = (state=initstate,action) => {
    switch(action.type){
        case FETCH :
            return {
                ...state,
                data: action.payload
            }

        case DELETE :
            let {data} = state;

            data.forEach((_id,index) => {
                if(data._id === action.payload){
                    data.splice(index,1)
                }
            });
            return {
                ...state,
                data: data
            }

        case SEARCH :
        return {
            ...state,
            data: action.payload
        }

        default:
            return state;
    }
}
