import {FETCH,DELETE,SEARCH,RESET} from '../actionType/actionType';

//初始化数据
const initstate = {
    data:'',
    list:''
}

//最终的数据处理结果
export const todolist = (state=initstate,action) => {
    switch(action.type){

        //获取页面数据
        case FETCH :
            return {
                ...state,
                data: action.payload,
                list: action.payload,
            }

        //删除
        case DELETE :
            let {data} = state;
            console.log(data)
            data.forEach((item,index) => {
                if(item._id === action.payload){
                    data.splice(index,1)
                }
            });
            return {
                ...state,
                data: data
            }

        //搜索
        case SEARCH :
            let value = action.payload;
            let datas = state.data;
            let dataArr = [];
            for (let i in datas){
                if(datas[i].name.search(value) >= 0){
                    dataArr.push({
                        _id:datas[i]._id,
                        name:datas[i].name,
                        desc:datas[i].desc,
                        url:datas[i].url,
                        type:datas[i].type,
                    })
                }
            }    
            return {
                ...state,
                data: dataArr
        }

        //重置
        case RESET :
            return {
                ...state,
                list: action.payload,
            }

        default:
            return state;

    }
}
